                Text(
                    text = if (isCompleted) "" else "⏸️",
                    fontSize = 60.sp
                )
                Spacer(modifier = Modifier.height(8.dp))
                Text(
                    text = if (isCompleted) "목표 달성" else "중단됨",
                    fontSize = 14.sp,
                    color = if (isCompleted) Color(0xFF4CAF50) else Color(0xFFFF9800),
                    fontWeight = FontWeight.Bold
                )
            }
        }

        Spacer(modifier = Modifier.height(32.dp))

        // 서브 통계 (2줄 배치)
        Column {
            // 첫 번째 줄
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceEvenly
            ) {
                CardSubStatItem(
                    value = "${totalDuration}일",
                    label = "총 금주 기간",
                    modifier = Modifier.weight(1f)
                )
                CardSubStatItem(
                    value = String.format(Locale.getDefault(), "%,d원", savedMoney),
                    label = "절약한 금액",
                    modifier = Modifier.weight(1f)
                )
                CardSubStatItem(
                    value = "${savedHours}시간",
                    label = "절약한 시간",
                    modifier = Modifier.weight(1f)
                )
            }

            Spacer(modifier = Modifier.height(24.dp))

            // 두 번째 줄
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceEvenly
            ) {
                CardSubStatItem(
                    value = "${((actualDays.toFloat() / targetDays) * 100).roundToInt()}%",
                    label = "목표 달성률",
                    modifier = Modifier.weight(1f)
                )
                CardSubStatItem(
                    value = getLevelName(actualDays),
                    label = "달성 레벨",
                    modifier = Modifier.weight(1f)
                )
                CardSubStatItem(
                    value = "${(actualDays / 30.0).roundToInt()}일",
                    label = "기대 수명 증가",
                    modifier = Modifier.weight(1f)
                )
            }
        }

        Spacer(modifier = Modifier.weight(1f))

        // 하단 버튼
        Button(
            onClick = { onBack() },
            modifier = Modifier
                .fillMaxWidth()
                .height(56.dp),
            colors = ButtonDefaults.buttonColors(
                containerColor = Color.Black
            )
        ) {
            Text(
                text = "확인",
                fontSize = 18.sp,
                fontWeight = FontWeight.Bold,
