package com.example.alcoholictimer.adapter

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.example.alcoholictimer.R
import com.example.alcoholictimer.model.RecentActivity

class RecentActivityAdapter : RecyclerView.Adapter<RecentActivityAdapter.ViewHolder>() {
    private val activities = ArrayList<RecentActivity>()

    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val ivActivityIcon: ImageView = itemView.findViewById(R.id.ivActivityIcon)
        val tvEndDate: TextView = itemView.findViewById(R.id.tvEndDate)
        val tvActivityTitle: TextView = itemView.findViewById(R.id.tvActivityTitle)
        val tvDuration: TextView = itemView.findViewById(R.id.tvDuration)
        val tvStatus: TextView = itemView.findViewById(R.id.tvStatus)
        val tvSavedMoney: TextView = itemView.findViewById(R.id.tvSavedMoney)
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_recent_activity, parent, false)
        return ViewHolder(view)
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val activity = activities[position]

        holder.tvEndDate.text = activity.endDate
        holder.tvActivityTitle.text = activity.title
        holder.tvDuration.text = "${activity.duration}일 ${activity.hours}시간"
        holder.tvStatus.text = if(activity.isSuccess) "성공" else "실패"
        holder.tvSavedMoney.text = "${activity.savedMoney}만원 절약"
    }

    override fun getItemCount() = activities.size

    fun updateActivities(newActivities: List<RecentActivity>) {
        activities.clear()
        activities.addAll(newActivities.take(5)) // 최대 5개까지만 표시
        notifyDataSetChanged()
    }
}

package com.example.alcoholictimer.components

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import java.util.*

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun MonthPickerBottomSheet(
    isVisible: Boolean,
    onDismiss: () -> Unit,
    onMonthPicked: (year: Int, month: Int) -> Unit,
    initialYear: Int = Calendar.getInstance().get(Calendar.YEAR),
    initialMonth: Int = Calendar.getInstance().get(Calendar.MONTH) + 1 // Calendar.MONTH는 0부터 시작
) {
    var selectedYear by remember { mutableStateOf(initialYear) }
    var selectedMonth by remember { mutableStateOf(initialMonth) }

    val bottomSheetState = rememberModalBottomSheetState(
        skipPartiallyExpanded = true
    )

    if (isVisible) {
        ModalBottomSheet(
            onDismissRequest = onDismiss,
            sheetState = bottomSheetState,
            containerColor = Color.White,
            shape = RoundedCornerShape(topStart = 20.dp, topEnd = 20.dp),
            dragHandle = {
                Box(
                    modifier = Modifier
                        .width(40.dp)
                        .height(4.dp)
                        .background(
                            Color.Gray.copy(alpha = 0.4f),
                            RoundedCornerShape(2.dp)
                        )
                )
            }
        ) {
            Column(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(24.dp),
                horizontalAlignment = Alignment.CenterHorizontally
            ) {
                // 상단: 이전월 표시와 안내
                Row(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(bottom = 16.dp),
                    horizontalArrangement = Arrangement.SpaceBetween,
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    // 이전월 표시
                    val calendar = Calendar.getInstance()
                    calendar.add(Calendar.MONTH, -1)
                    val prevMonth = calendar.get(Calendar.MONTH) + 1

                    Text(
                        text = "${prevMonth}월",
                        fontSize = 16.sp,
                        color = Color.Gray,
                        fontWeight = FontWeight.Normal
                    )

                    Spacer(modifier = Modifier.weight(1f))

                    // 안내 텍스트
                    Text(
                        text = "년/월 선택",
                        fontSize = 14.sp,
                        color = Color.Gray
                    )
                }

                // 가운데: NumberPicker 2개 (연도, 월)
                Row(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(vertical = 24.dp),
                    horizontalArrangement = Arrangement.SpaceEvenly,
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    // 연도 선택
                    Column(
                        horizontalAlignment = Alignment.CenterHorizontally
                    ) {
                        Text(
                            text = "연도",
                            fontSize = 14.sp,
                            color = Color.Gray,
                            modifier = Modifier.padding(bottom = 8.dp)
                        )
                        NumberPicker(
                            value = selectedYear,
                            onValueChange = { selectedYear = it },
                            range = 2000..2030,
                            displayValues = (2000..2030).map { "${it}년" },
                            modifier = Modifier.width(120.dp)
                        )
                    }

                    // 구분선
                    Box(
                        modifier = Modifier
                            .width(1.dp)
                            .height(120.dp)
                            .background(Color.Gray.copy(alpha = 0.3f))
                    )

                    // 월 선택
                    Column(
                        horizontalAlignment = Alignment.CenterHorizontally
                    ) {
                        Text(
                            text = "월",
                            fontSize = 14.sp,
                            color = Color.Gray,
                            modifier = Modifier.padding(bottom = 8.dp)
                        )
                        NumberPicker(
                            value = selectedMonth,
                            onValueChange = { selectedMonth = it },
                            range = 1..12,
                            displayValues = (1..12).map { "${it}월" },
                            modifier = Modifier.width(100.dp)
                        )
                    }
                }

                // 하단: 선택 버튼
                Button(
                    onClick = {
                        onMonthPicked(selectedYear, selectedMonth)
                        onDismiss()
                    },
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(56.dp)
                        .padding(top = 16.dp),
                    colors = ButtonDefaults.buttonColors(
                        containerColor = Color.Black,
                        contentColor = Color.White
                    ),
                    shape = RoundedCornerShape(16.dp)
                ) {
                    Text(
                        text = "선택",
                        fontSize = 18.sp,
                        fontWeight = FontWeight.Bold
                    )
                }

                // 하단 여백
                Spacer(modifier = Modifier.height(16.dp))
            }
        }
    }
}

package com.example.alcoholictimer.components

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import java.util.*

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun MonthPickerBottomSheet(
    isVisible: Boolean,
    onDismiss: () -> Unit,
    onMonthPicked: (year: Int, month: Int) -> Unit,
    initialYear: Int = Calendar.getInstance().get(Calendar.YEAR),
    initialMonth: Int = Calendar.getInstance().get(Calendar.MONTH) + 1
) {
    // 현재와 과거 4개월 목록 생성
    val monthOptions = remember {
        generateMonthOptions()
    }
    
    // 기본값을 현재 월(첫 번째 인덱스)로 설정
    var selectedMonthIndex by remember { mutableStateOf(0) }

    val bottomSheetState = rememberModalBottomSheetState(
        skipPartiallyExpanded = true
    )

    if (isVisible) {
        ModalBottomSheet(
            onDismissRequest = onDismiss,
            sheetState = bottomSheetState,
            containerColor = Color.White,
            shape = RoundedCornerShape(topStart = 20.dp, topEnd = 20.dp),
            dragHandle = {
                Box(
                    modifier = Modifier
                        .width(40.dp)
                        .height(4.dp)
                        .background(
                            Color.Gray.copy(alpha = 0.4f),
                            RoundedCornerShape(2.dp)
                        )
                )
            }
        ) {
            Column(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(24.dp),
                horizontalAlignment = Alignment.CenterHorizontally
            ) {
                // 상단: 이전월 표시와 안내
                Row(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(bottom = 16.dp),
                    horizontalArrangement = Arrangement.SpaceBetween,
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    // 이전월 표시
                    Text(
                        text = if (monthOptions.size > 1) monthOptions[1].displayText else "",
                        fontSize = 16.sp,
                        color = Color.Gray,
                        fontWeight = FontWeight.Normal
                    )

                    Spacer(modifier = Modifier.weight(1f))

                    // 안내 텍스트
                    Text(
                        text = "월 선택",
                        fontSize = 14.sp,
                        color = Color.Gray
                    )
                }

                // 가운데: 월 선택 NumberPicker
                Column(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(vertical = 24.dp),
                    horizontalAlignment = Alignment.CenterHorizontally
                ) {
                    Text(
                        text = "월 선택",
                        fontSize = 14.sp,
                        color = Color.Gray,
                        modifier = Modifier.padding(bottom = 8.dp)
                    )

                    NumberPicker(
                        value = selectedMonthIndex,
                        onValueChange = { selectedMonthIndex = it },
                        range = 0 until monthOptions.size,
                        displayValues = monthOptions.map { it.displayText },
                        modifier = Modifier.width(200.dp)
                    )
                }

                // 하단: 선택 버튼
                Button(
                    onClick = {
                        val selectedMonth = monthOptions[selectedMonthIndex]
                        onMonthPicked(selectedMonth.year, selectedMonth.month)
                        onDismiss()
                    },
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(56.dp)
                        .padding(top = 16.dp),
                    colors = ButtonDefaults.buttonColors(
                        containerColor = Color.Black,
                        contentColor = Color.White
                    ),
                    shape = RoundedCornerShape(16.dp)
                ) {
                    Text(
                        text = "선택",
                        fontSize = 18.sp,
                        fontWeight = FontWeight.Bold
                    )
                }

                // 하단 여백
                Spacer(modifier = Modifier.height(16.dp))
            }
        }
    }
}

data class MonthOption(
    val year: Int,
    val month: Int,
    val displayText: String
)

private fun generateMonthOptions(): List<MonthOption> {
    val calendar = Calendar.getInstance()
    val options = mutableListOf<MonthOption>()

    // 현재 월부터 시작해서 과거로 거슬러 올라가면서 4개월 생성
    for (i in 0 until 4) {
        val year = calendar.get(Calendar.YEAR)
        val month = calendar.get(Calendar.MONTH) + 1 // Calendar.MONTH는 0부터 시작

        val displayText = "${year}년 ${month}월"
        options.add(MonthOption(year, month, displayText))

        // 이전 달로 이동
        calendar.add(Calendar.MONTH, -1)
    }

    // 현재에서 과거 순으로 반환 (현재 월이 첫 번째)
    return options
}

