import androidx.compose.ui.unit.dp
import androidx.lifecycle.ViewModel
import androidx.lifecycle.ViewModelProvider
import androidx.lifecycle.viewmodel.compose.viewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow

class GraphActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContent {
            GraphScreen()
        }
    }
}

class GraphViewModel(context: Context) : ViewModel() {
    private val _weeklyRecords = MutableStateFlow(listOf(0, 0, 0, 0, 0, 0, 0))
    val weeklyRecords: StateFlow<List<Int>> = _weeklyRecords

    init {
        // SharedPreferences에서 금주 기록 데이터 불러오기 (예시)
        val prefs = context.getSharedPreferences("records_prefs", Context.MODE_PRIVATE)
        val records = (0..6).map { day ->
            prefs.getInt("record_day_$day", 0)
        }
        _weeklyRecords.value = records
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun GraphScreen() {
    val context = LocalContext.current
    val viewModel: GraphViewModel = ViewModelProvider(context as ComponentActivity).get(GraphViewModel::class.java)
    val weeklyRecords by viewModel.weeklyRecords.collectAsState()
    Scaffold(
        topBar = {
            TopAppBar(title = { Text("금주 기록 그래프") })
        }
    ) { padding ->
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(padding)
        ) {
            BarChart(
                data = weeklyRecords,
                modifier = Modifier
                    .fillMaxWidth()
                    .height(200.dp)
                    .padding(16.dp)
            )
        }
    }
}

// BarChart 컴포저블 구현
@Composable
fun BarChart(data: List<Int>, modifier: Modifier = Modifier) {
    Row(
        modifier = modifier,
        horizontalArrangement = Arrangement.SpaceEvenly
    ) {
        data.forEach { value ->
            Box(
                modifier = Modifier
                    .weight(1f)
                    .fillMaxHeight()
                    .padding(horizontal = 4.dp),
                contentAlignment = Alignment.BottomCenter
            ) {
                Box(
                    modifier = Modifier
                        .width(24.dp)
                        .height((value * 20).dp)
                        .background(MaterialTheme.colorScheme.primary)
                )
            }
        }
    }
}

