option.value:
package com.example.alcoholictimer

import android.content.Intent
import android.os.Bundle
import android.widget.ImageButton
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.WindowCompat
import androidx.drawerlayout.widget.DrawerLayout
import com.google.android.material.navigation.NavigationView

/**
 * 모든 액티비티의 베이스 클래스
 * 공통된 햄버거 메뉴와 네비게이션 기능을 제공합니다.
 */
abstract class BaseActivity : AppCompatActivity() {

    protected lateinit var drawerLayout: DrawerLayout
    protected lateinit var navigationView: NavigationView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        // 시스템 UI와의 겹침 처리를 위한 설정
        WindowCompat.setDecorFitsSystemWindows(window, false)

        setContentView(R.layout.layout_base)

        // 공통 UI 요소 초기화
        drawerLayout = findViewById(R.id.drawerLayout)
        navigationView = findViewById(R.id.navigationView)
        val btnMenu = findViewById<ImageButton>(R.id.btnMenu)

        // 메뉴 버튼 클릭 시 드로어 열기
        btnMenu.setOnClickListener {
            drawerLayout.open()
        }

        // 내비게이션 메뉴 아이템 클릭 이벤트
        navigationView.setNavigationItemSelectedListener { menuItem ->
            when (menuItem.itemId) {
                R.id.nav_start -> {
                    if (this !is StartActivity) {
                        startActivity(Intent(this, StartActivity::class.java))
                        if (this !is MainActivity) {
                            finish()
                        }
                    }
                }
                R.id.nav_status -> {
                    if (this !is StatusActivity) {
                        startActivity(Intent(this, StatusActivity::class.java))
                        if (this !is MainActivity) {
                            finish()
                        }
                    }
                }
            }
            drawerLayout.close()
            true
        }

        // 각 액티비티의 레이아웃을 contentFrame에 추가
        setupContentView()
    }

    /**
     * 각 액티비티에서 구현할 추상 메소드
     * 각자의 레이아웃을 contentFrame에 추가하는 작업을 수행
     */
    protected abstract fun setupContentView()
}

package com.example.alcoholictimer

import android.content.Intent
import android.os.Bundle
import android.widget.ImageButton
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.WindowCompat
import androidx.drawerlayout.widget.DrawerLayout
import com.google.android.material.navigation.NavigationView

/**
 * 모든 액티비티의 베이스 클래스
 * 공통된 햄버거 메뉴와 네비게이션 기능을 제공합니다.
 */
abstract class BaseActivity : AppCompatActivity() {

    protected lateinit var drawerLayout: DrawerLayout
    protected lateinit var navigationView: NavigationView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        // 시스템 UI와의 겹침 처리를 위한 설정
        WindowCompat.setDecorFitsSystemWindows(window, false)

        setContentView(R.layout.layout_base)

        // 공통 UI 요소 초기화
        drawerLayout = findViewById(R.id.drawerLayout)
        navigationView = findViewById(R.id.navigationView)
        val btnMenu = findViewById<ImageButton>(R.id.btnMenu)

        // 메뉴 버튼 클릭 시 드로어 열기
        btnMenu.setOnClickListener {
            // 드로어를 열기 전에 금주 상태를 확인하여 메뉴 아이템 활성화/비활성화 설정
            updateNavigationMenuState()
            drawerLayout.open()
        }

        // 내비게이션 메뉴 아이템 클릭 이벤트
        navigationView.setNavigationItemSelectedListener { menuItem ->
            when (menuItem.itemId) {
                R.id.nav_start -> {
                    if (this !is StartActivity) {
                        startActivity(Intent(this, StartActivity::class.java))
                        if (this !is MainActivity) {
                            finish()
                        }
                    }
                }
                R.id.nav_status -> {
                    if (this !is StatusActivity) {
                        startActivity(Intent(this, StatusActivity::class.java))
                        if (this !is MainActivity) {
                            finish()
                        }
                    }
                }
            }
            drawerLayout.close()
            true
        }

        // 각 액티비티의 레이아웃을 contentFrame에 추가
        setupContentView()
    }

    /**
     * 금주 상태에 따라 내비게이션 메뉴 상태를 업데이트합니다.
     * 이미 금주를 시작했다면 금주 시작 메뉴를 비활성화합니다.
     */
    private fun updateNavigationMenuState() {
        val sharedPref = getSharedPreferences("user_settings", MODE_PRIVATE)
        val hasStarted = sharedPref.contains("start_time")

        // 금주가 이미 시작되었으면 시작 메뉴 비활성화
        val startMenuItem = navigationView.menu.findItem(R.id.nav_start)
        startMenuItem.isEnabled = !hasStarted

        // 금주가 시작되었을 때만 상태 메뉴 활성화
        val statusMenuItem = navigationView.menu.findItem(R.id.nav_status)
        statusMenuItem.isEnabled = hasStarted
    }

    /**
     * 각 액티비티에서 구현할 추상 메소드
     * 각자의 레이아웃을 contentFrame에 추가하는 작업을 수행
     */
    protected abstract fun setupContentView()
}

package com.example.alcoholictimer

import android.os.Bundle
import android.view.LayoutInflater
import android.view.ViewGroup

class MainActivity : BaseActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        // BaseActivity에서 상속받은 메서드로 나머지 기능 처리됨
    }

    override fun setupContentView() {
        // MainActivity의 컨텐츠를 contentFrame에 추가
        val contentFrame = findViewById<ViewGroup>(R.id.contentFrame)
        LayoutInflater.from(this).inflate(R.layout.content_main, contentFrame, true)
    }
}

package com.example.alcoholictimer

import android.content.Intent
import android.os.Bundle
import android.view.LayoutInflater
import android.view.ViewGroup

class MainActivity : BaseActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        // 금주 타이머가 진행 중인지 확인
        val sharedPref = getSharedPreferences("user_settings", MODE_PRIVATE)
        val isSobrietyStarted = sharedPref.contains("start_time")

        // 금주가 진행 중이면 StatusActivity로 자동 이동
        if (isSobrietyStarted) {
            val intent = Intent(this, StatusActivity::class.java)
            startActivity(intent)
            // MainActivity는 종료하지 않고 백스택에 남겨둠
        }
    }

    override fun setupContentView() {
        // MainActivity의 컨텐츠를 contentFrame에 추가
        val contentFrame = findViewById<ViewGroup>(R.id.contentFrame)
        LayoutInflater.from(this).inflate(R.layout.content_main, contentFrame, true)
    }
}

package com.example.alcoholictimer

import android.content.Context
import android.content.Intent
import android.os.Bundle
import android.view.Menu
import android.view.MenuItem
import android.widget.Button
import android.widget.EditText
import android.widget.ImageButton
import android.widget.Toast
import androidx.appcompat.app.ActionBarDrawerToggle
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.GravityCompat
import androidx.drawerlayout.widget.DrawerLayout
import com.google.android.material.navigation.NavigationView

class StartActivity : AppCompatActivity() {
    private lateinit var drawerLayout: DrawerLayout

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        try {
            setContentView(R.layout.activity_start)

            // 툴바 설정
            val toolbar = findViewById<androidx.appcompat.widget.Toolbar>(R.id.toolbar)
            setSupportActionBar(toolbar)
            supportActionBar?.title = "금주 타이머"

            // 기존 홈버튼(햄버거) 제거
            supportActionBar?.setDisplayHomeAsUpEnabled(false)
            supportActionBar?.setHomeButtonEnabled(false)

            // 드로어 레이아웃 초기화
            drawerLayout = findViewById(R.id.drawerLayout)

            // 별도의 메뉴 버튼 설정
            val menuButton = findViewById<ImageButton>(R.id.menuButton)
            menuButton.setOnClickListener {
                // 버튼 클릭시 드로어 열기
                drawerLayout.openDrawer(GravityCompat.START)
            }

            // 네비게이션 뷰 설정
            val navigationView = findViewById<NavigationView>(R.id.navigationView)
            navigationView.setNavigationItemSelectedListener { menuItem ->
                when (menuItem.itemId) {
                    R.id.action_settings -> {
                        Toast.makeText(this, "설정 메뉴 클릭됨", Toast.LENGTH_SHORT).show()
                        drawerLayout.closeDrawer(GravityCompat.START)
                        true
                    }
                    R.id.action_about -> {
                        Toast.makeText(this, "정보 메뉴 클릭됨", Toast.LENGTH_SHORT).show()
                        drawerLayout.closeDrawer(GravityCompat.START)
                        true
                    }
                    else -> false
                }
            }

            // 목표일 입력: 숫자 입력(EditText)만 사용
            val editTextTargetDays = findViewById<EditText>(R.id.editTextTargetDays)
            val btnSave = findViewById<Button>(R.id.btnSave)

            editTextTargetDays.setOnClickListener {
                editTextTargetDays.isFocusable = true
                editTextTargetDays.isFocusableInTouchMode = true
                editTextTargetDays.requestFocus()
                val imm = getSystemService(Context.INPUT_METHOD_SERVICE) as android.view.inputmethod.InputMethodManager
                imm.showSoftInput(editTextTargetDays, 0)
            }

            editTextTargetDays.setOnFocusChangeListener { v, hasFocus ->
                if (!hasFocus) {
                    editTextTargetDays.isFocusable = false
                    editTextTargetDays.isFocusableInTouchMode = false
                }
            }

            btnSave.setOnClickListener {
                try {
                    val today = java.time.LocalDate.now().toString()
                    val targetDaysStr = editTextTargetDays.text.toString().trim()
                    val targetDays = if (targetDaysStr.isNotEmpty()) targetDaysStr.toInt() else 0
                    val targetDate = java.time.LocalDate.now().plusDays(targetDays.toLong()).toString()

                    val sharedPref = getSharedPreferences("AlcoholicPrefs", Context.MODE_PRIVATE)
                    val editor = sharedPref.edit()
                    editor.putString("start_date", today)
                    editor.putString("target_date", targetDate)
                    editor.apply()

                    val intent = Intent(this, StatusActivity::class.java)
                    startActivity(intent)
                    finish()
                } catch (e: Exception) {
                    Toast.makeText(this, "저장 오류: " + e.message, Toast.LENGTH_LONG).show()
                }
            }
        } catch (e: Exception) {
            Toast.makeText(this, "초기화 오류: " + e.message, Toast.LENGTH_LONG).show()
        }
    }

    override fun onCreateOptionsMenu(menu: Menu): Boolean {
        // 메뉴 인플레이션 (옵션 메뉴에는 아이템을 표시하지 않음)
        return true
    }

    override fun onBackPressed() {
        // 뒤로가기 버튼 처리 - 드로어가 열려있으면 닫기
        if (drawerLayout.isDrawerOpen(GravityCompat.START)) {
            drawerLayout.closeDrawer(GravityCompat.START)
        } else {
            super.onBackPressed()
        }
    }
}

package com.example.alcoholictimer

import android.content.Intent
import android.os.Bundle
import android.view.LayoutInflater
import android.view.ViewGroup
import android.widget.Button
import android.widget.EditText
import android.widget.Toast

class StartActivity : BaseActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        // BaseActivity에서 이미 햄버거 메뉴 및 네비게이션 기능 처리됨
    }

    override fun setupContentView() {
        // StartActivity 고유의 컨텐츠를 contentFrame에 추가
        val contentFrame = findViewById<ViewGroup>(R.id.contentFrame)
        val view = LayoutInflater.from(this).inflate(R.layout.content_start, contentFrame, true)

        // 버튼 및 기타 UI 요소에 대한 이벤트 처리
        val btnStart = view.findViewById<Button>(R.id.btnStart)
        val editTextDays = view.findViewById<EditText>(R.id.editTextDays)

        btnStart.setOnClickListener {
            val days = editTextDays.text.toString().toIntOrNull() ?: 0

            if (days > 0) {
                // 사용자 설정을 SharedPreferences에 저장
                val sharedPref = getSharedPreferences("user_settings", MODE_PRIVATE)
                with(sharedPref.edit()) {
                    putInt("target_days", days)
                    putLong("start_time", System.currentTimeMillis())
                    apply()
                }

                Toast.makeText(this, "${days}일 동안 금주를 시작합니다!", Toast.LENGTH_SHORT).show()
                val intent = Intent(this, StatusActivity::class.java)
                startActivity(intent)
                finish()
            } else {
                Toast.makeText(this, "1일 이상의 숫자를 입력해주세요", Toast.LENGTH_SHORT).show()
            }
        }
    }
}

package com.example.alcoholictimer

import android.app.AlertDialog
import android.content.Intent
import android.graphics.Color
import android.os.Bundle
import android.view.LayoutInflater
import android.view.ViewGroup
import android.widget.ProgressBar
import android.widget.TextView
import android.widget.Toast
import com.google.android.material.floatingactionbutton.FloatingActionButton

class StatusActivity : BaseActivity() {
    private val levelMilestones = listOf(0, 7, 14, 30, 60, 120, 240, 365)
    private val levelTitles = listOf(
        "새싹 도전자",
        "첫걸음 성공",
        "의지의 시작",
        "한달의 기적",
        "습관의 탄생",
        "의지의 달인",
        "금주의 마스터",
        "절제의 달인"
    )
    private val levelColors = listOf(
        "#FF9E9E9E",  // Gray
        "#FF4CAF50",  // Green
        "#FF2196F3",  // Blue
        "#FF9C27B0",  // Purple
        "#FFFF9800",  // Orange
        "#FFFF5722",  // Deep Orange
        "#FF3F51B5",  // Indigo
        "#FFE91E63"   // Pink
    )

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        // BaseActivity에서 이미 햄버거 메뉴 및 네비게이션 기능 처리됨
    }

    override fun setupContentView() {
        // StatusActivity 고유의 컨텐츠를 contentFrame에 추가
        val contentFrame = findViewById<ViewGroup>(R.id.contentFrame)
        val view = LayoutInflater.from(this).inflate(R.layout.content_status, contentFrame, true)

        val tvDaysCount = view.findViewById<TextView>(R.id.tvDaysCount)
        val tvLevel = view.findViewById<TextView>(R.id.tvLevel)
        val tvLevelTitle = view.findViewById<TextView>(R.id.tvLevelTitle)
        val tvNextLevel = view.findViewById<TextView>(R.id.tvNextLevel)
        val tvMessage = view.findViewById<TextView>(R.id.tvMessage)
        val progressLevel = view.findViewById<ProgressBar>(R.id.progressLevel)

        // 중지 버튼 설정
        val btnStopSobriety = view.findViewById<FloatingActionButton>(R.id.btnStopSobriety)
        btnStopSobriety.setOnClickListener {
            // 확인 대화상자 표시 (취소/확인 버튼 포함)
            AlertDialog.Builder(this)
                .setTitle("금주 중지")
                .setMessage("정말 금주를 중지하시겠습니까? 모든 금주 기록이 초기화됩니다.")
                .setNegativeButton("취소") { dialog, _ ->
                    // 취소 버튼 클릭 시 대화상자만 닫힘
                    dialog.dismiss()
                }
                .setPositiveButton("확인") { _, _ ->
                    // SharedPreferences 데이터 초기화
                    val sharedPref = getSharedPreferences("user_settings", MODE_PRIVATE)
                    with(sharedPref.edit()) {
                        clear()  // 모든 데이터 삭제
                        apply()
                    }

                    Toast.makeText(this, "금주가 초기화되었습니다.", Toast.LENGTH_SHORT).show()

                    // 메인 화면으로 이동
                    val intent = Intent(this, MainActivity::class.java)
                    intent.flags = Intent.FLAG_ACTIVITY_CLEAR_TOP or Intent.FLAG_ACTIVITY_NEW_TASK
                    startActivity(intent)
                    finish()
                }
                .setCancelable(true)
                .show()
        }

        // SharedPreferences에서 데이터 불러오기
        val sharedPref = getSharedPreferences("user_settings", MODE_PRIVATE)
        val startTime = sharedPref.getLong("start_time", System.currentTimeMillis())
        val targetDays = sharedPref.getInt("target_days", 30)

        // 경과일 계산 (밀리초를 일수로 변환)
        val daysPassed = ((System.currentTimeMillis() - startTime) / (1000 * 60 * 60 * 24)).toInt()

        // UI 업데이트
        tvDaysCount.text = daysPassed.toString()

        // 레벨 계산
        var currentLevel = 0
        for (i in levelMilestones.indices) {
            if (daysPassed >= levelMilestones[i]) {
                currentLevel = i
            } else {
                break
            }
        }

        // 레벨 정보 표시
        tvLevel.text = "Lv. ${currentLevel + 1}"
        tvLevelTitle.text = levelTitles[currentLevel]
        tvLevelTitle.setTextColor(Color.parseColor(levelColors[currentLevel]))

        // 다음 레벨 정보
        if (currentLevel < levelMilestones.size - 1) {
            val nextLevelDays = levelMilestones[currentLevel + 1]
            val daysLeft = nextLevelDays - daysPassed
            tvNextLevel.text = "다음 레벨까지 ${daysLeft}일"

            // 프로그레스바 업데이트
            val currentLevelDays = levelMilestones[currentLevel]
            val nextLevelThreshold = levelMilestones[currentLevel + 1]
            val progress = ((daysPassed - currentLevelDays).toFloat() / (nextLevelThreshold - currentLevelDays)) * 100
            progressLevel.progress = progress.toInt()
        } else {
            tvNextLevel.text = "최고 레벨 달성!"
            progressLevel.progress = 100
        }

        // 목표 달성 여부 메시지
        if (daysPassed >= targetDays) {
            tvMessage.text = "축하합니다! ${targetDays}일 목표를 달성했습니다!"
        } else {
            tvMessage.text = "목표까지 ${targetDays - daysPassed}일 남았습니다. 힘내세요!"
        }
    }
}

package com.example.alcoholictimer

import android.app.Dialog
import android.content.Intent
