        else -> 2.5
    }

    val drinkHoursVal = when(selectedDuration) {
        "짧음" -> 2
        "보통" -> 4
        "김" -> 6
        else -> 4
    }

    val hangoverHoursVal = 5
    val weeks = actualDays / 7.0
    val savedMoney = (weeks * freqVal * costVal).roundToInt()
    val savedHours = (weeks * freqVal * (drinkHoursVal + hangoverHoursVal)).roundToInt()

    // 레벨에 따른 배경색
    val backgroundColor = when {
        actualDays < 7 -> Color(0xFFF5F5F5)
        actualDays < 30 -> Color(0xFFFFF3CD)
        actualDays < 90 -> Color(0xFFE7F3FF)
        actualDays < 365 -> Color(0xFFE8F5E8)
        else -> Color(0xFFFFF0DC)
    }

    Column(
        modifier = Modifier
            .fillMaxSize()
            .background(Color.White)
            .padding(24.dp)
    ) {
        // 헤더 영역
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(start = 0.dp), // 왼쪽 패딩 추가
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            // 뒤로가기 버튼 (화살표 아이콘만)
            Box(
                contentAlignment = Alignment.CenterStart,
                modifier = Modifier
                    .size(40.dp)
                    .clickable {
                        onBack()
                    }
            ) {
                Icon(
                    imageVector = Icons.Outlined.ArrowBack,
                    contentDescription = "뒤로가기",
                    tint = Color.Black
                )
            }

            // 타이틀 + 삭제 아이콘 그룹을 Row로 묶어서 최대 너비로 확장
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = "금주 기록 상세",
                    fontSize = 22.sp,
                    fontWeight = FontWeight.Bold,
                    color = Color.Black,
                    modifier = Modifier
                        .weight(1f)
                        .padding(start = 8.dp) // 왼쪽 패딩 추가
                )
                Box(
                    contentAlignment = Alignment.Center,
                    modifier = Modifier
                        .padding(end = 0.dp)
                        .size(35.dp) // 전체 클릭 영역 크기
                        .clip(CircleShape) // 원형으로 클리핑하여 리플 효과도 원형으로 만듦
                        .clickable { showDeleteDialog = true }
                ) {
                    Icon(
                        imageVector = Icons.Outlined.Delete,
                        contentDescription = "기록 삭제",
                        tint = Color.Black,
                        modifier = Modifier.size(30.dp) // 아이콘 자체 크기는 유지
                    )
                }
            }
        }

        // 삭제 경고 다이얼로그
        if (showDeleteDialog) {
            AlertDialog(
                onDismissRequest = { showDeleteDialog = false },
                title = { Text("기록 삭제") },
                text = { Text("정말로 이 금주 기록을 삭제하시겠습니까?\n삭제 후 복구할 수 없습니다.") },
                confirmButton = {
                    TextButton(onClick = {
                        // 다이얼로그 먼저 닫기
                        showDeleteDialog = false
                        // 삭제 실행
                        try {
                            deleteRecord(context, startTime, endTime)
                        } catch (e: Exception) {
                            Log.e("DetailActivity", "삭제 중 오류", e)
                        }
                        // 즉시 액티비티 종료
                        (context as? DetailActivity)?.finish()
                    }) {
                        Text("삭제", color = Color.Red)
                    }
                },
                dismissButton = {
                    TextButton(onClick = { showDeleteDialog = false }) {
                        Text("취소")
                    }
                }
            )
        }

        // 구분선 추가
        Spacer(modifier = Modifier.height(16.dp))
        Divider(
            modifier = Modifier.fillMaxWidth(),
            thickness = 1.dp,
            color = Color(0xFFE0E0E0)
        )

        Spacer(modifier = Modifier.height(32.dp))

        // 날짜 및 시간
        Text(
            text = displayDateTime,
            fontSize = 16.sp,
            color = Color.Gray,
            modifier = Modifier.fillMaxWidth()
        )

        Spacer(modifier = Modifier.height(8.dp))

        // 기록 제목
        Text(
            text = recordTitle,
            fontSize = 20.sp,
            fontWeight = FontWeight.Bold,
            modifier = Modifier.fillMaxWidth()
        )

        // 기록 제목 아래 구분선 추가
        Spacer(modifier = Modifier.height(16.dp))
        Divider(
            modifier = Modifier.fillMaxWidth(),
            thickness = 1.dp,
            color = Color(0xFFE0E0E0)
        )

        Spacer(modifier = Modifier.height(40.dp))

        // 주요 통계 영역 - 트로피 아이콘 부분 제거
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.Start // SpaceBetween에서 Start로 변경
        ) {
            // 대형 숫자만 표시 (오른쪽 트로피 아이콘 제거)
            Column(
                horizontalAlignment = Alignment.Start
            ) {
                Text(
                    text = actualDays.toString(),
                    fontSize = 72.sp,
                    fontWeight = FontWeight.Bold,
                    color = Color.Black,
                    lineHeight = 72.sp,
                    modifier = Modifier.padding(start = 0.dp) // 왼쪽 패딩 추가
                )
                Text(
                    text = "일",
                    fontSize = 16.sp,
                    color = Color.Gray,
                    modifier = Modifier.padding(start = 0.dp) // 왼쪽 패딩 추가
                )
            }
        }

        Spacer(modifier = Modifier.height(32.dp))

        // 서브 통계 (2줄 배치)
        Column {
            // 첫 번째 줄
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceEvenly
            ) {
                SubStatItem(
                    value = "${totalDuration}일",
                    label = "총 금주 기간",
                    modifier = Modifier.weight(1f)
                )
                SubStatItem(
                    value = String.format("%,d원", savedMoney),
                    label = "절약한 금액",
                    modifier = Modifier.weight(1.3f)
                )
                SubStatItem(
                    value = "${savedHours}시간",
                    label = "절약한 시간",
                    modifier = Modifier.weight(0.7f)
                )
            }

            Spacer(modifier = Modifier.height(24.dp))

            // 두 번째 줄
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceEvenly
            ) {
                SubStatItem(
                    value = "${((actualDays.toFloat() / targetDays) * 100).roundToInt()}%",
                    label = "목표 달성률",
                    modifier = Modifier.weight(1f)
                )
                SubStatItem(
                    value = getLevelName(actualDays),
                    label = "달성 레벨",
                    modifier = Modifier.weight(1.3f)
                )
                SubStatItem(
                    value = "+${(actualDays / 30.0).roundToInt()}일",
                    label = "기대 수명 증가",
                    modifier = Modifier.weight(0.7f)
                )
            }
        }

        Spacer(modifier = Modifier.weight(1f))

        // 확인 버튼 제거 - 하단 여백만 유지
        Spacer(modifier = Modifier.height(24.dp))
    }
}

@Composable
fun SubStatItem(
    value: String,
    label: String,
    modifier: Modifier = Modifier,
    alignEnd: Boolean = false // 정렬 방향 파라미터 추가
) {
    Column(
        modifier = modifier,
        horizontalAlignment = if (alignEnd) Alignment.End else Alignment.Start // 정렬 방향 선택
    ) {
        Text(
            text = value,
            fontSize = 18.sp,
            fontWeight = FontWeight.Bold,
            color = Color.Black,
            textAlign = if (alignEnd) TextAlign.End else TextAlign.Start // 정렬 방향 선택
        )
        Spacer(modifier = Modifier.height(4.dp))
        Text(
            text = label,
            fontSize = 12.sp,
            color = Color.Gray,
            textAlign = if (alignEnd) TextAlign.End else TextAlign.Start // 정렬 방향 선택
        )
    }
}

// 레벨명 함수
private fun getLevelName(days: Int): String {
    return when {
        days < 7 -> "시작"
        days < 30 -> "작심 7일"
        days < 90 -> "한 달 클리어"
        days < 365 -> "3개월 클리어"
        else -> "절제의 레전드"
    }
}

