@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun StatusScreenContent(
    timePassed: Int,
    timeUnitText: String,
    hoursDisplay: String,
    timeDetail: String,
    progressValue: Float,
    statusMessage: String,
    onStopClick: () -> Unit,
    onMenuClick: () -> Unit
) {
    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("금주 상태", fontSize = 20.sp) },
                navigationIcon = {
                    IconButton(onClick = onMenuClick) {
                        Icon(
                            imageVector = Icons.Default.Menu,
                            contentDescription = "메뉴 열기",
                            tint = MaterialTheme.colorScheme.onSurface
                        )
                    }
                }
            )
        }
    ) { paddingValues ->
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(paddingValues)
                .padding(24.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Text(
                text = "금주 진행 상황",
                fontSize = 24.sp,
                fontWeight = FontWeight.Bold,
                modifier = Modifier.padding(bottom = 16.dp)
            )

            // 대형 숫자 표시
            Text(
                text = timePassed.toString(),
                fontSize = 48.sp,
                fontWeight = FontWeight.Bold,
                color = MaterialTheme.colorScheme.primary,
                modifier = Modifier.padding(bottom = 8.dp)
            )

            // 시간 단위 표시
            Text(
                text = timeUnitText,
                fontSize = 18.sp,
                color = MaterialTheme.colorScheme.onSurfaceVariant,
                modifier = Modifier.padding(bottom = 16.dp)
            )

            // 시간 표시
            Text(
                text = hoursDisplay,
                fontSize = 20.sp,
                fontWeight = FontWeight.Medium,
                modifier = Modifier.padding(bottom = 8.dp)
            )

            // 상세 시간 표시
            Text(
                text = timeDetail,
                fontSize = 16.sp,
                color = MaterialTheme.colorScheme.onSurfaceVariant,
                modifier = Modifier.padding(bottom = 16.dp)
            )

            // 진행률 표시
            LinearProgressIndicator(
                progress = { progressValue },
                modifier = Modifier
                    .fillMaxWidth()
                    .height(8.dp)
                    .padding(bottom = 16.dp)
            )

            // 상태 메시지
            Text(
                text = statusMessage,
                fontSize = 16.sp,
                modifier = Modifier.padding(bottom = 24.dp)
            )

            // 중지 버튼
            Button(
                onClick = onStopClick,
                colors = ButtonDefaults.buttonColors(
                    containerColor = MaterialTheme.colorScheme.error
                )
            ) {
                Text("금주 중지", fontSize = 16.sp)
            }
        }
    }
}

package com.example.alcoholictimer

import android.content.Context
import android.os.Bundle
import android.util.Log
import android.widget.Toast
import androidx.activity.compose.setContent
import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.platform.LocalContext
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.tooling.preview.Preview
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import com.example.alcoholictimer.utils.Constants

class TestActivity : BaseActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContent {
            BaseScreen {
                SettingsScreen()
            }
        }
    }

    override fun getScreenTitle(): String = "설정"

    @Composable
    private fun SettingsScreen() {
        val context = LocalContext.current
        var selectedMode by remember { mutableStateOf(Constants.TEST_MODE_REAL) }

        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(16.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Text(
                text = "설정",
                fontSize = 24.sp,
                fontWeight = FontWeight.Bold,
                modifier = Modifier.padding(bottom = 16.dp)
            )
            Text(
                text = "테스트 모드 선택",
                fontSize = 18.sp,
                modifier = Modifier.padding(bottom = 8.dp)
            )
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceEvenly
            ) {
                RadioButton(
                    selected = selectedMode == Constants.TEST_MODE_REAL,
                    onClick = { selectedMode = Constants.TEST_MODE_REAL }
                )
                Text("실제 모드")
                RadioButton(
                    selected = selectedMode == Constants.TEST_MODE_MINUTE,
                    onClick = { selectedMode = Constants.TEST_MODE_MINUTE }
                )
                Text("분 모드")
                RadioButton(
                    selected = selectedMode == Constants.TEST_MODE_SECOND,
                    onClick = { selectedMode = Constants.TEST_MODE_SECOND }
                )
                Text("초 모드")
            }
            Spacer(modifier = Modifier.height(24.dp))
            Button(onClick = {
                saveSettings(selectedMode)
                Toast.makeText(context, "설정이 저장되었습니다.", Toast.LENGTH_SHORT).show()
            }) {
                Text("저장", fontSize = 18.sp)
            }
        }
    }

    private fun saveSettings(selectedMode: Int) {
        val preferences = getSharedPreferences(Constants.PREFS_NAME, Context.MODE_PRIVATE)
        val editor = preferences.edit()
        editor.putInt(Constants.PREF_KEY_TEST_MODE, selectedMode)
        val success = editor.commit()
        Log.d("TestActivity", "Settings saved. Mode: $selectedMode, Success: $success")

        // Constants 클래스의 동적 설정 업데이트
        Constants.updateTestMode(selectedMode)
    }

    @Preview(showBackground = true)
    @Composable
    fun PreviewSettingsScreen() {
        BaseScreen {
            SettingsScreen()
        }
    }
}

package com.example.alcoholictimer

import android.content.Context
import android.os.Bundle
import android.util.Log
import android.widget.Toast
import androidx.activity.compose.setContent
import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.platform.LocalContext
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.tooling.preview.Preview
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import com.example.alcoholictimer.utils.Constants

class TestActivity : BaseActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContent {
            BaseScreen {
                SettingsScreen()
            }
        }
    }

    override fun getScreenTitle(): String = "설정"

    @Composable
    private fun SettingsScreen() {
        val context = LocalContext.current
        var selectedMode by remember { mutableStateOf(Constants.TEST_MODE_REAL) }

        Column(
            modifier = Modifier
                .fillMaxSize()
                .background(Color.White)
                .padding(16.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Text(
                text = "설정",
                fontSize = 24.sp,
                fontWeight = FontWeight.Bold,
                modifier = Modifier.padding(bottom = 16.dp)
            )
            Text(
                text = "테스트 모드 선택",
                fontSize = 18.sp,
                modifier = Modifier.padding(bottom = 8.dp)
            )
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceEvenly
            ) {
                RadioButton(
                    selected = selectedMode == Constants.TEST_MODE_REAL,
                    onClick = { selectedMode = Constants.TEST_MODE_REAL }
                )
                Text("실제 모드")
                RadioButton(
                    selected = selectedMode == Constants.TEST_MODE_MINUTE,
                    onClick = { selectedMode = Constants.TEST_MODE_MINUTE }
                )
                Text("분 모드")
                RadioButton(
                    selected = selectedMode == Constants.TEST_MODE_SECOND,
                    onClick = { selectedMode = Constants.TEST_MODE_SECOND }
                )
                Text("초 모드")
            }
            Spacer(modifier = Modifier.height(24.dp))
            Button(onClick = {
                saveSettings(selectedMode)
                Toast.makeText(context, "설정이 저장되었습니다.", Toast.LENGTH_SHORT).show()
            }) {
                Text("저장", fontSize = 18.sp)
            }
        }
    }

    private fun saveSettings(selectedMode: Int) {
        val preferences = getSharedPreferences(Constants.PREFS_NAME, Context.MODE_PRIVATE)
        val editor = preferences.edit()
        editor.putInt(Constants.PREF_KEY_TEST_MODE, selectedMode)
        val success = editor.commit()
        Log.d("TestActivity", "Settings saved. Mode: $selectedMode, Success: $success")

        // Constants 클래스의 동적 설정 업데이트
        Constants.updateTestMode(selectedMode)
    }

    @Preview(showBackground = true)
    @Composable
    fun PreviewSettingsScreen() {
        BaseScreen {
            SettingsScreen()
        }
    }
}

package com.example.alcoholictimer.adapter

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.example.alcoholictimer.R
import com.example.alcoholictimer.model.RecentActivity

class RecentActivityAdapter : RecyclerView.Adapter<RecentActivityAdapter.ViewHolder>() {
    private val activities = ArrayList<RecentActivity>()

    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val ivActivityIcon: ImageView = itemView.findViewById(R.id.ivActivityIcon)
        val tvEndDate: TextView = itemView.findViewById(R.id.tvEndDate)
        val tvActivityTitle: TextView = itemView.findViewById(R.id.tvActivityTitle)
        val tvDuration: TextView = itemView.findViewById(R.id.tvDuration)
        val tvStatus: TextView = itemView.findViewById(R.id.tvStatus)
        val tvSavedMoney: TextView = itemView.findViewById(R.id.tvSavedMoney)
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_recent_activity, parent, false)
        return ViewHolder(view)
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val activity = activities[position]

        holder.tvEndDate.text = activity.endDate
        holder.tvActivityTitle.text = activity.title
        holder.tvDuration.text = "${activity.duration}일 ${activity.hours}시간"
        holder.tvStatus.text = if(activity.isSuccess) "성공" else "실패"
        holder.tvSavedMoney.text = "${activity.savedMoney}만원 절약"
    }

    override fun getItemCount() = activities.size

    fun updateActivities(newActivities: List<RecentActivity>) {
        activities.clear()
        activities.addAll(newActivities.take(5)) // 최대 5개까지만 표시
        notifyDataSetChanged()
    }
}
