                    modifier = Modifier.padding(bottom = 24.dp)
                )

                // 커스텀 입력 필드 (3자리 숫자 넓이)
                Box(
                    modifier = Modifier.width(150.dp),
                    contentAlignment = Alignment.Center
                ) {
                    Column {
                        BasicTextField(
                            value = inputText,
                            onValueChange = {
                                inputText = it
                                errorText = ""
                            },
                            textStyle = LocalTextStyle.current.copy(
                                fontSize = 48.sp,
                                fontWeight = FontWeight.Bold,
                                textAlign = TextAlign.Center,
                                color = if (isValid) MaterialTheme.colorScheme.primary else Color.Black
                            ),
                            keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number),
                            singleLine = true,
                            cursorBrush = SolidColor(MaterialTheme.colorScheme.primary),
                            modifier = Modifier
                                .fillMaxWidth()
                                .padding(bottom = 8.dp)
                        )

                        // 밑줄 (얇고 검은색)
                        Canvas(
                            modifier = Modifier
                                .fillMaxWidth()
                                .height(1.dp)
                        ) {
                            drawLine(
                                color = Color.Black,
                                start = Offset(0f, 0f),
                                end = Offset(size.width, 0f),
                                strokeWidth = 2.dp.toPx(),
                                cap = StrokeCap.Square
                            )
                        }
                    }
                }

                // 단위 표시
                Text(
                    text = "일",
                    fontSize = 20.sp,
                    color = Color.Gray,
                    modifier = Modifier.padding(top = 16.dp)
                )
            }

            Spacer(modifier = Modifier.height(60.dp))

            // 플레이 버튼
            FloatingActionButton(
                onClick = {
                    val targetTime = inputText.toIntOrNull() ?: 0
                    if (targetTime > 0) {
                        val sharedPref = context.getSharedPreferences("user_settings", MODE_PRIVATE)
                        sharedPref.edit().apply {
                            putInt("target_days", targetTime)
                            putLong("start_time", System.currentTimeMillis())
                            putBoolean("timer_completed", false)
                            apply()
                        }
                        val intent = Intent(context, StatusActivity::class.java)
                        context.startActivity(intent)
                    }
                },
                modifier = Modifier.size(100.dp),
                shape = CircleShape,
                containerColor = if (isValid) MaterialTheme.colorScheme.primary else Color.Gray
            ) {
                Icon(
                    imageVector = Icons.Default.PlayArrow,
                    contentDescription = "시작",
                    modifier = Modifier.size(50.dp),
                    tint = Color.White
                )
            }
        }
    }

    override fun onResume() {
        super.onResume()
        updateTimeModeDisplay()
    }

    private fun updateTimeModeDisplay() {
        // SharedPreferences에서 현재 테스트 모드를 읽어옴
        val sharedPref = getSharedPreferences(Constants.PREFS_NAME, MODE_PRIVATE)
        val currentTestMode = sharedPref.getInt(Constants.PREF_TEST_MODE, Constants.TEST_MODE_REAL)

        // Constants의 현재 테스트 모드를 업데이트
        Constants.updateTestMode(currentTestMode)

        val timeUnitText = when (currentTestMode) {
            Constants.TEST_MODE_SECOND -> "금주 목표 초수"
            Constants.TEST_MODE_MINUTE -> "금주 목표 분수"
            else -> "금주 목표 일수"
        }
    }

    @Preview(showBackground = true)
    @Composable
    fun PreviewStartScreen() {
        BaseScreen {
            StartScreen()
        }
    }
}

package com.example.alcoholictimer.adapter

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.example.alcoholictimer.R
import com.example.alcoholictimer.model.RecentActivity

class RecentActivityAdapter : RecyclerView.Adapter<RecentActivityAdapter.ViewHolder>() {
    private val activities = ArrayList<RecentActivity>()

    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val ivActivityIcon: ImageView = itemView.findViewById(R.id.ivActivityIcon)
        val tvEndDate: TextView = itemView.findViewById(R.id.tvEndDate)
        val tvActivityTitle: TextView = itemView.findViewById(R.id.tvActivityTitle)
        val tvDuration: TextView = itemView.findViewById(R.id.tvDuration)
        val tvStatus: TextView = itemView.findViewById(R.id.tvStatus)
        val tvSavedMoney: TextView = itemView.findViewById(R.id.tvSavedMoney)
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_recent_activity, parent, false)
        return ViewHolder(view)
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val activity = activities[position]

        holder.tvEndDate.text = activity.endDate
        holder.tvActivityTitle.text = activity.title
        holder.tvDuration.text = "${activity.duration}일 ${activity.hours}시간"
        holder.tvStatus.text = if(activity.isSuccess) "성공" else "실패"
        holder.tvSavedMoney.text = "${activity.savedMoney}만원 절약"
    }

    override fun getItemCount() = activities.size

    fun updateActivities(newActivities: List<RecentActivity>) {
        activities.clear()
        activities.addAll(newActivities.take(5)) // 최대 5개까지만 표시
        notifyDataSetChanged()
    }
}
